mapper.select:
select count(0)
from user
where username = #{name};

mapper.insert:
insert into user(username,
                 password,
                 likename,
                 create_time,
                 update_time
                 )
values (#{userName},
        #{password},
        #{likeName},
        #{createTime},
        #{updateTime}
        );

mapper.update:
        update user set
                        password = #{password},
                        update_time =#{updateTime}
where id =#{id};

update user set
                img_id = #{imgId},
